import React, { useState } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/styles';
import {
  Card,
  CardHeader,
  CardContent,
  Typography,
  Radio,
  colors
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {},
  option: {
    border: `1px solid ${theme.palette.divider}`,
    display: 'flex',
    alignItems: 'flex-start',
    padding: theme.spacing(2),
    maxWidth: 560,
    '& + &': {
      marginTop: theme.spacing(2)
    }
  },
  selectedOption: {
    backgroundColor: colors.grey[50]
  },
  optionRadio: {
    margin: -10
  },
  optionDetails: {
    marginLeft: theme.spacing(2)
  }
}));

const options = [
  {
    value: 'freelancer',
    title: 'I\'m a freelancer',
    description: 'I\'m looking for teamates to join in a personal project'
  },
  {
    value: 'projectOwner',
    title: 'I’m a project owner',
    description:
      'I\'m looking for freelancer or contractors to take care of my project'
  },
  {
    value: 'affiliate',
    title: 'I want to join affiliate',
    description:
      'I\'m looking for freelancer or contractors to take care of my project'
  }
];

function AboutAuthor({ className, ...rest }) {
  const classes = useStyles();
  const [selected, setSelected] = useState(options[0].value);

  const handleChange = (event, option) => {
    setSelected(option.value);
  };

  return (
    <Card
      {...rest}
      className={clsx(classes.root, className)}
    >
      <CardHeader title="Who are you exactly?" />
      <CardContent>
        {options.map((option) => (
          <div
            className={clsx(classes.option, {
              [classes.selectedOption]: selected === option.value
            })}
            key={option.value}
          >
            <Radio
              checked={selected === option.value}
              className={classes.optionRadio}
              color="primary"
              onClick={(event) => handleChange(event, option)}
            />
            <div className={classes.optionDetails}>
              <Typography
                gutterBottom
                variant="h5"
              >
                {option.title}
              </Typography>
              <Typography variant="body1">{option.description}</Typography>
            </div>
          </div>
        ))}
      </CardContent>
    </Card>
  );
}

AboutAuthor.propTypes = {
  className: PropTypes.string
};

export default AboutAuthor;
